import { createSlice } from "@reduxjs/toolkit";

const cartSlide = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    totalQuantity: 0
  },
  reducers: {
    addToCart(state, action) {
      debugger
      const newItem = action.payload
      const existItem = state.items.find((item) => item.id === newItem.id)

      state.totalQuantity++
      if (!existItem) {
        state.items.push({
          id: newItem.id,
          price: newItem.price,
          total: 1,
          totalPrice: newItem.price,
          name: newItem.title
        })
      } else {
        existItem.quantity++
        existItem.totalPrice = existItem.totalPrice + existItem.price

      }
    },
    removeFromCart(state, action) {
      const idRemove = action.payload
      const existItem = state.items.find(item => item.id === idRemove)
      state.totalQuantity--
      if (existItem.totalQantity === 1) {
        state.items = state.items.filter((item) => item.id !== idRemove)
      } else {
        existItem.quantity--
      }
    }
  }
})


export const cartActions = cartSlide.actions
export default cartSlide
