import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { uiActions } from './store/ui-slice';

let isInitial = true
function App() {
  const showCart = useSelector(state => state.ui.cartIsVisiable)
  const cart = useSelector(state => state.cart)
  const notifycation = useSelector(state => state.ui.notifycation)
  const dispatch = useDispatch()

  useEffect(() => {
    const sendCartData = async () => {
      dispatch(uiActions.showNotification({
        status: 'Pending',
        title: 'send data',
        message: 'Sending cart data'
      }))

      const response = await fetch('https://fir-react-app-886c3-default-rtdb.firebaseio.com/cart.json', {
        method: 'PUT',
        body: JSON.stringify(cart)
      })

      if (!response.ok) {
        throw new Error('Sending cart data failed')
      }
      dispatch(uiActions.showNotification({
        status: 'Success',
        title: 'Success!',
        message: 'Success send data'
      }))
    }
    if (isInitial) {
      isInitial = false
      return
    }

    sendCartData.catch((error) => {
      dispatch(uiActions.showNotification({
        status: 'Error',
        title: 'Error',
        message: 'Sending cart data failed'
      })
      )
    })
  }, [cart], dispatch)
  return (
    <Layout>
      {showCart && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
